import React, { useState, useEffect } from "react";
import { Link, useLocation } from "react-router-dom";

function ListPage() {
  const [albums, setAlbums] = useState([]);
  const [searchQuery, setSearchQuery] = useState("");
  const [minRating, setMinRating] = useState("All");
  const location = useLocation(); // To determine the active page

  useEffect(() => {
    async function fetchAlbums() {
      const response = await fetch(
        "https://6728860f270bd0b97555efb5.mockapi.io/albums"
      );
      const data = await response.json();
      setAlbums(data);
    }
    fetchAlbums();
  }, []);

  const headerStyle = {
    backgroundColor: "#111111",
    color: "#fff",
    padding: "20px",
    fontWeight: "bold",
    fontSize: "24px",
    display: "flex",
    alignItems: "center",
    justifyContent: "flex-start", // Left justify the content
    gap: "20px",
    fontFamily: "sans-serif",
  };

  const navLinkStyle = {
    color: "#AEAEAE",
    textDecoration: "none",
    fontSize: "16px",
    fontFamily: "sans-serif",
  };

  const activeNavLinkStyle = {
    color: "#fff",
  };

  const filterContainerStyle = {
    margin: "20px auto",
    textAlign: "center",
    fontFamily: "sans-serif",
  };

  const inputStyle = {
    padding: "8px",
    marginRight: "10px",
  };

  const filteredAlbums = albums.filter((album) => {
    const searchMatch = album.name
      .toLowerCase()
      .includes(searchQuery.toLowerCase());
    const minRatingValue = minRating === "All" ? 0 : Number(minRating);
    const ratingMatch = (album.rating || 0) >= minRatingValue;
    return searchMatch && ratingMatch;
  });

  const gridContainerStyle = {
    display: "grid",
    gridTemplateColumns: "repeat(4, 1fr)", // Four albums per row
    gap: "24px", // Spacing between items
    padding: "20px",
    margin: "0 auto",
    maxWidth: "90%",
    width: "100%",
  };

  const albumImageStyle = {
    width: "100%", // Responsive width
    height: "auto", // Maintain aspect ratio
    borderRadius: "8px",
  };

  const pageStyle = {
    backgroundColor: "#111111",
    color: "#fff",
    fontFamily: "sans-serif",
    minHeight: "100vh",
  };

  return (
    <div style={pageStyle}>
      <style>
        {`
          a:hover {
            color: #fff !important;
          }
          @media (max-width: 768px) {
            .responsive-container {
              grid-template-columns: repeat(2, 1fr) !important; /* Two per row on smaller screens */
            }
          }
          @media (max-width: 480px) {
            .responsive-container {
              grid-template-columns: repeat(1, 1fr) !important; /* One per row on very small screens */
            }
          }
        `}
      </style>

      {/* Navbar */}
      <div style={headerStyle}>
        <div>Music Search</div>
        <Link
          to="/"
          style={{
            ...navLinkStyle,
            ...(location.pathname === "/" ? activeNavLinkStyle : {}),
          }}
        >
          Search
        </Link>
        <Link
          to="/list"
          style={{
            ...navLinkStyle,
            ...(location.pathname === "/list" ? activeNavLinkStyle : {}),
          }}
        >
          My Albums
        </Link>
      </div>

      <div style={filterContainerStyle}>
        <input
          type="text"
          placeholder="Search Album"
          value={searchQuery}
          onChange={(e) => setSearchQuery(e.target.value)}
          style={inputStyle}
        />

        <select
          value={minRating}
          onChange={(e) => setMinRating(e.target.value)}
          style={inputStyle}
        >
          <option value="All">All Ratings</option>
          <option value="1">1 and up</option>
          <option value="2">2 and up</option>
          <option value="3">3 and up</option>
          <option value="4">4 and up</option>
          <option value="5">5 only</option>
        </select>
      </div>

      <div
        style={gridContainerStyle}
        className="responsive-container" // Apply media query styles for responsiveness
      >
        {filteredAlbums.length === 0 ? (
          <p>No albums match your search.</p>
        ) : (
          filteredAlbums.map((album) => {
            const albumCover =
              album.images && album.images.length > 0
                ? album.images[0].url
                : null;

            return (
              albumCover && (
                <Link to={`/albums/${album.id}`} key={album.id}>
                  <img
                    src={albumCover}
                    alt={album.name}
                    style={albumImageStyle}
                  />
                </Link>
              )
            );
          })
        )}
      </div>
    </div>
  );
}

export default ListPage;
