import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";

function ListPage() {
  const [albums, setAlbums] = useState([]);
  const [searchQuery, setSearchQuery] = useState("");
  const [minRating, setMinRating] = useState("All");

  useEffect(() => {
    async function fetchAlbums() {
      const response = await fetch(
        "https://6728860f270bd0b97555efb5.mockapi.io/albums"
      );
      const data = await response.json();
      setAlbums(data);
    }
    fetchAlbums();
  }, []);

  const headerStyle = {
    backgroundColor: "#000",
    color: "#fff",
    padding: "20px",
    fontWeight: "bold",
    fontSize: "24px",
    display: "flex",
    alignItems: "center",
    justifyContent: "flex-start", // Left justify the content
    gap: "20px", // Space between items
  };

  const navLinkStyle = {
    color: "#fff",
    textDecoration: "none",
    fontSize: "16px",
  };

  const filterContainerStyle = {
    margin: "20px auto",
    textAlign: "center",
  };

  const inputStyle = {
    padding: "8px",
    marginRight: "10px",
  };

  const filteredAlbums = albums.filter((album) => {
    const searchMatch = album.name
      .toLowerCase()
      .includes(searchQuery.toLowerCase());
    const minRatingValue = minRating === "All" ? 0 : Number(minRating);
    const ratingMatch = (album.rating || 0) >= minRatingValue;
    return searchMatch && ratingMatch;
  });

  const gridContainerStyle = {
    display: "grid",
    gridTemplateColumns: "repeat(auto-fill, minmax(150px, 1fr))",
    gap: "16px",
    padding: "20px",
    margin: "0 auto",
    maxWidth: "70%",
    width: "100%",
  };

  return (
    <div>
      <style>
        {`
          @media (max-width: 600px) {
            .responsive-container {
              max-width: 100% !important;
            }
          }
        `}
      </style>

      {/* Navbar */}
      <div style={headerStyle}>
        <div>Music Search</div>
        {/* Add the links next to "Music Search" */}
        <Link to="/" style={navLinkStyle}>
          Search
        </Link>
        <Link to="/list" style={navLinkStyle}>
          My Albums
        </Link>
      </div>

      <div style={filterContainerStyle}>
        <input
          type="text"
          placeholder="Search Album"
          value={searchQuery}
          onChange={(e) => setSearchQuery(e.target.value)}
          style={inputStyle}
        />

        <select
          value={minRating}
          onChange={(e) => setMinRating(e.target.value)}
          style={inputStyle}
        >
          <option value="All">All Ratings</option>
          <option value="1">1 and up</option>
          <option value="2">2 and up</option>
          <option value="3">3 and up</option>
          <option value="4">4 and up</option>
          <option value="5">5 only</option>
        </select>
      </div>

      <div style={gridContainerStyle} className="responsive-container">
        {filteredAlbums.length === 0 ? (
          <p>No albums match your search.</p>
        ) : (
          filteredAlbums.map((album) => {
            const albumCover =
              album.images && album.images.length > 0
                ? album.images[0].url
                : null;

            return (
              albumCover && (
                <Link to={`/albums/${album.id}`} key={album.id}>
                  <img
                    src={albumCover}
                    alt={album.name}
                    style={{
                      width: "100%",
                      height: "auto",
                      borderRadius: "4px",
                    }}
                  />
                </Link>
              )
            );
          })
        )}
      </div>
    </div>
  );
}

export default ListPage;
