import React, { useState, useEffect, useCallback, useMemo } from "react";
import { useLocation } from "react-router-dom";
import DetailPage from "./DetailPage";

function ListPage() {
  const [albums, setAlbums] = useState([]);
  const [hoveredAlbum, setHoveredAlbum] = useState(null); // track which album is hovered
  const [searchQuery, setSearchQuery] = useState("");
  const [minRating, setMinRating] = useState("All");
  const [selectedAlbum, setSelectedAlbum] = useState(null); // track selected album for DetailPage
  const location = useLocation();

  useEffect(() => {
    async function fetchAlbums() {
      try {
        const response = await fetch(
          "https://6728860f270bd0b97555efb5.mockapi.io/albums"
        );
        if (!response.ok) {
          throw new Error("Failed to fetch albums.");
        }
        const data = await response.json();
        setAlbums(data);
      } catch (error) {
        console.error(error);
        alert("Error fetching albums.");
      }
    }
    fetchAlbums();
  }, []);

  const handleSearchInput = useCallback((value) => {
    setSearchQuery(value);
  }, []);

  const handleMinRatingChange = useCallback((value) => {
    setMinRating(value);
  }, []);

  const filteredAlbums = useMemo(() => {
    const query = searchQuery.toLowerCase();
    return albums.filter((album) => {
      const albumMatch = album.name.toLowerCase().includes(query);
      const artistMatch =
        album.artists &&
        album.artists.some((artist) =>
          artist.name.toLowerCase().includes(query)
        );
      const genreMatch =
        album.genres &&
        album.genres.some((genre) => genre.toLowerCase().includes(query));
      const trackMatch =
        album.tracks &&
        album.tracks.some((track) => track.name.toLowerCase().includes(query));
      const ratingMatch =
        minRating === "All" ||
        (album.rating && album.rating >= Number(minRating));

      return (
        (albumMatch || artistMatch || genreMatch || trackMatch) && ratingMatch
      );
    });
  }, [albums, searchQuery, minRating]);

  const gridContainerStyle = {
    display: "grid",
    gridTemplateColumns: "repeat(4, 1fr)",
    gap: "7px",
    padding: "20px",
    margin: "0 auto",
    maxWidth: "95%",
    width: "100%",
  };

  const albumContainerStyle = {
    position: "relative",
    overflow: "hidden",
    height: "210px",
    cursor: "pointer",
    boxShadow: "0 4px 6px rgba(0, 0, 0, 0.1)",
    transition: "transform 0.3s",
  };

  const albumImageStyle = (isHovered) => ({
    width: "100%",
    height: "100%",
    objectFit: "cover",
    opacity: isHovered ? 0.7 : 1, // dim only the hovered image
    transition: "opacity 0.3s ease",
  });

  const albumOverlayStyle = (isHovered) => ({
    position: "absolute",
    top: 0,
    left: 0,
    width: "100%",
    height: "100%",
    backgroundColor: "rgba(0, 0, 0, 0.5)",
    color: "#fff",
    display: "flex",
    alignItems: "center",
    justifyContent: "center",
    opacity: isHovered ? 1 : 0, // show overlay only on hover
    transition: "opacity 0.3s ease",
    fontSize: "14px",
    fontFamily: "sans-serif",
    fontWeight: "bold",
    textAlign: "center",
    padding: "10px",
  });

  const openDetailPage = (album) => {
    setSelectedAlbum(album);
  };

  const closeDetailPage = () => {
    setSelectedAlbum(null);
  };

  return (
    <div>
      <div>
        <input
          type="text"
          placeholder="Search albums"
          value={searchQuery}
          onChange={(e) => handleSearchInput(e.target.value)}
        />
        <select
          value={minRating}
          onChange={(e) => handleMinRatingChange(e.target.value)}
        >
          <option value="All">Filter Rating</option>
          <option value="1">1 and up</option>
          <option value="2">2 and up</option>
          <option value="3">3 and up</option>
          <option value="4">4 and up</option>
          <option value="5">5 only</option>
        </select>
      </div>

      <div style={gridContainerStyle}>
        {filteredAlbums.map((album) => {
          const isHovered = hoveredAlbum === album.id;
          return (
            <div
              key={album.id}
              style={albumContainerStyle}
              onMouseEnter={() => setHoveredAlbum(album.id)}
              onMouseLeave={() => setHoveredAlbum(null)}
              onClick={() => openDetailPage(album)}
            >
              <img
                src={album.images[0]?.url}
                alt={album.name}
                style={albumImageStyle(isHovered)}
              />
              <div style={albumOverlayStyle(isHovered)}>
                {album.name} | {album.artists.map((a) => a.name).join(", ")}
              </div>
            </div>
          );
        })}
      </div>

      {selectedAlbum && (
        <div
          style={{
            position: "fixed",
            top: 0,
            left: 0,
            width: "100%",
            height: "100%",
            backgroundColor: "rgba(0, 0, 0, 0.7)",
            display: "flex",
            justifyContent: "center",
            alignItems: "center",
            zIndex: 1000,
          }}
        >
          <div
            style={{
              backgroundColor: "#fff",
              borderRadius: "8px",
              width: "80%",
              height: "80%",
              overflow: "auto",
              position: "relative",
            }}
          >
            <button
              onClick={closeDetailPage}
              style={{
                position: "absolute",
                top: "10px",
                right: "10px",
                background: "red",
                color: "#fff",
                border: "none",
                borderRadius: "50%",
                width: "30px",
                height: "30px",
                fontSize: "16px",
                cursor: "pointer",
              }}
            >
              Ã—
            </button>
            <DetailPage album={selectedAlbum} />
          </div>
        </div>
      )}
    </div>
  );
}

export default ListPage;
