import React, { useState, useEffect } from "react";
import axios from "axios";
import { useParams, useNavigate } from "react-router-dom";

const EditPage = () => {
  const { id } = useParams(); // get coures id
  const navigate = useNavigate();

  const [formData, setFormData] = useState({
    cname: "",
    prof: "",
    credit: "",
    code: "",
  });
  const [errors, setErrors] = useState({});

  useEffect(() => {
    // Fetch the course data based on the ID
    const fetchCourse = async () => {
      try {
        const response = await axios.get(
          `https://6728860f270bd0b97555efb5.mockapi.io/courses/${id}`
        );
        setFormData(response.data);
      } catch (error) {
        console.error("Error fetching course:", error);
      }
    };
    fetchCourse();
  }, [id]);

  const validateForm = () => {
    const errors = {};
    if (!formData.cname.trim()) errors.cname = "Course name is required";
    if (!formData.prof.trim()) errors.prof = "Professor name is required";
    if (!formData.credit.trim()) errors.credit = "Credits are required";
    if (!formData.code.trim()) errors.code = "Course code is required";
    setErrors(errors);
    return Object.keys(errors).length === 0;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!validateForm()) return;

    try {
      await axios.put(
        `https://6728860f270bd0b97555efb5.mockapi.io/courses/${id}`,
        formData
      );
      navigate("/");
    } catch (error) {
      console.error("Error updating course:", error);
    }
  };

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  return (
    <div className="container my-5">
      <h1 className="text-center mb-4">Edit Course</h1>
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label>Course Name</label>
          <input
            type="text"
            name="cname"
            className="form-control"
            value={formData.cname}
            onChange={handleChange}
          />
          {errors.cname && (
            <small className="text-danger">{errors.cname}</small>
          )}
        </div>
        <div className="form-group">
          <label>Professor</label>
          <input
            type="text"
            name="prof"
            className="form-control"
            value={formData.prof}
            onChange={handleChange}
          />
          {errors.prof && <small className="text-danger">{errors.prof}</small>}
        </div>
        <div className="form-group">
          <label>Credits</label>
          <input
            type="text"
            name="credit"
            className="form-control"
            value={formData.credit}
            onChange={handleChange}
          />
          {errors.credit && (
            <small className="text-danger">{errors.credit}</small>
          )}
        </div>
        <div className="form-group">
          <label>Course Code</label>
          <input
            type="text"
            name="code"
            className="form-control"
            value={formData.code}
            onChange={handleChange}
          />
          {errors.code && <small className="text-danger">{errors.code}</small>}
        </div>
        <button type="submit" className="btn btn-primary btn-block mt-3">
          Update Course
        </button>
        <button
          type="button"
          className="btn btn-secondary btn-block mt-2"
          onClick={() => navigate("/")}
        >
          Cancel
        </button>
      </form>
    </div>
  );
};

export default EditPage;
