import React, { useEffect, useState } from "react";
import { useParams, Link } from "react-router-dom";

function DetailPage() {
  const { id } = useParams();
  const [album, setAlbum] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    async function fetchAlbum() {
      const response = await fetch(
        `https://6728860f270bd0b97555efb5.mockapi.io/albums/${id}`
      );
      const data = await response.json();
      setAlbum(data);
      setLoading(false);
    }

    fetchAlbum();
  }, [id]);

  if (loading) {
    return <div>Loading...</div>;
  }

  if (!album) {
    return <div>Album not found.</div>;
  }

  // Extract the details from the album
  const { name, album_type, artists, images } = album;

  const artistNames =
    artists && artists.length > 0
      ? artists.map((artist) => artist.name).join(", ")
      : "Unknown Artist";

  const albumCover = images && images.length > 0 ? images[0].url : "";

  return (
    <div style={{ padding: "20px" }}>
      <h1>{name}</h1>
      {albumCover && (
        <div>
          <img
            src={albumCover}
            alt={name}
            style={{ width: "300px", height: "auto", borderRadius: "8px" }}
          />
        </div>
      )}
      <p>
        <strong>Artist:</strong> {artistNames}
      </p>
      <p>
        <strong>Album Type:</strong> {album_type}
      </p>

      <Link to="/list">Back to My Albums</Link>
    </div>
  );
}

export default DetailPage;
