import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";

function ListPage() {
  const [albums, setAlbums] = useState([]);
  const [searchQuery, setSearchQuery] = useState("");
  const [minRating, setMinRating] = useState("All");

  useEffect(() => {
    async function fetchAlbums() {
      const response = await fetch(
        "https://6728860f270bd0b97555efb5.mockapi.io/albums"
      );
      const data = await response.json();
      setAlbums(data);
    }
    fetchAlbums();
  }, []);

  // Navbar styling (same as search page)
  const headerStyle = {
    backgroundColor: "#000",
    color: "#fff",
    padding: "20px",
    fontWeight: "bold",
    fontSize: "24px",
    display: "flex",
    alignItems: "center",
    justifyContent: "center",
  };

  // Center the search and rating
  const filterContainerStyle = {
    margin: "20px auto",
    textAlign: "center",
  };

  const inputStyle = {
    padding: "8px",
    marginRight: "10px",
  };

  const filteredAlbums = albums.filter((album) => {
    // Adjust filtering logic as needed
    const searchMatch = album.name
      .toLowerCase()
      .includes(searchQuery.toLowerCase());
    const minRatingValue = minRating === "All" ? 0 : Number(minRating);
    const ratingMatch = (album.rating || 0) >= minRatingValue;
    return searchMatch && ratingMatch;
  });

  const listContainerStyle = {
    margin: "20px auto",
    maxWidth: "70%",
    width: "100%",
  };

  return (
    <div>
      <style>
        {`
          @media (max-width: 600px) {
            .responsive-container {
              max-width: 100% !important;
            }
          }
        `}
      </style>
      <div style={headerStyle}>
        <div>Music Search</div>
      </div>

      <div style={filterContainerStyle}>
        <input
          type="text"
          placeholder="Search Albums..."
          value={searchQuery}
          onChange={(e) => setSearchQuery(e.target.value)}
          style={inputStyle}
        />

        <select
          value={minRating}
          onChange={(e) => setMinRating(e.target.value)}
          style={inputStyle}
        >
          <option value="All">All Ratings</option>
          <option value="1">1 and up</option>
          <option value="2">2 and up</option>
          <option value="3">3 and up</option>
          <option value="4">4 and up</option>
          <option value="5">5 only</option>
        </select>
      </div>

      <div style={listContainerStyle} className="responsive-container">
        {filteredAlbums.length === 0 ? (
          <p>No albums match your search.</p>
        ) : (
          <ul style={{ listStyle: "none", padding: 0 }}>
            {filteredAlbums.map((album) => {
              const albumCover =
                album.images && album.images[0] ? album.images[0].url : null;
              return (
                <li
                  key={album.id}
                  style={{ borderBottom: "1px solid #ccc", padding: "10px 0" }}
                >
                  {albumCover && (
                    <img
                      src={albumCover}
                      alt={album.name}
                      width="60"
                      height="60"
                      style={{
                        borderRadius: "4px",
                        marginRight: "10px",
                        verticalAlign: "middle",
                      }}
                    />
                  )}
                  <strong>{album.name}</strong> - Rating:{" "}
                  {album.rating || "N/A"}
                </li>
              );
            })}
          </ul>
        )}
      </div>
    </div>
  );
}

export default ListPage;
