import React, { useEffect, useState } from "react";
import { useParams, Link } from "react-router-dom";

function DetailPage() {
  const { id } = useParams();
  const [album, setAlbum] = useState(null);
  const [loading, setLoading] = useState(true);

  const [tracks, setTracks] = useState([]);
  const [tracksLoading, setTracksLoading] = useState(false);

  const token = process.env.REACT_APP_SPOTIFY_TOKEN;

  // State for rating form
  const [newRating, setNewRating] = useState(""); // store as a string initially

  useEffect(() => {
    async function fetchAlbum() {
      const response = await fetch(
        `https://6728860f270bd0b97555efb5.mockapi.io/albums/${id}`
      );
      const data = await response.json();
      setAlbum(data);
      setLoading(false);

      // If album already has a rating, set it in the state
      if (data.rating) {
        setNewRating(String(data.rating));
      }
    }

    fetchAlbum();
  }, [id]);

  useEffect(() => {
    async function fetchTracks() {
      if (!album || !album.spotifyId || !token) return;

      setTracksLoading(true);
      const response = await fetch(
        `https://api.spotify.com/v1/albums/${album.spotifyId}/tracks`,
        {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        }
      );
      const data = await response.json();
      setTracks(data.items || []);
      setTracksLoading(false);
    }

    fetchTracks();
  }, [album, token]);

  if (loading) {
    return <div>Loading album details...</div>;
  }

  if (!album) {
    return <div>Album not found.</div>;
  }

  const { name, album_type, artists, images, rating } = album;

  const artistNames =
    artists && artists.length > 0
      ? artists.map((artist) => artist.name).join(", ")
      : "Unknown Artist";

  const albumCover = images && images.length > 0 ? images[0].url : "";

  // Handle rating submission
  const handleRatingSubmit = async (e) => {
    e.preventDefault();
    if (!newRating) return;

    // Patch the album with the new rating
    const response = await fetch(
      `https://6728860f270bd0b97555efb5.mockapi.io/albums/${id}`,
      {
        method: "PATCH",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({ rating: Number(newRating) }),
      }
    );

    if (response.ok) {
      const updatedAlbum = await response.json();
      setAlbum(updatedAlbum);
      alert("Rating updated successfully!");
    } else {
      alert("Failed to update rating.");
    }
  };

  return (
    <div style={{ padding: "20px" }}>
      <h1>{name}</h1>
      {albumCover && (
        <div style={{ marginBottom: "20px" }}>
          <img
            src={albumCover}
            alt={name}
            style={{ width: "300px", height: "auto", borderRadius: "8px" }}
          />
        </div>
      )}
      <p>
        <strong>Artist:</strong> {artistNames}
      </p>
      <p>
        <strong>Album Type:</strong> {album_type}
      </p>

      <h2>Tracks</h2>
      {tracksLoading && <p>Loading tracks...</p>}
      {!tracksLoading && tracks.length === 0 && <p>No tracks found.</p>}
      {!tracksLoading && tracks.length > 0 && (
        <ol>
          {tracks.map((track) => (
            <li key={track.id}>{track.name}</li>
          ))}
        </ol>
      )}

      <h2>Rating</h2>
      {rating ? (
        <p>Current Rating: {rating} out of 5</p>
      ) : (
        <p>No rating yet. Be the first to rate this album.</p>
      )}

      <form onSubmit={handleRatingSubmit} style={{ marginTop: "10px" }}>
        <label>
          Set Rating (1-5):
          <input
            type="number"
            min="1"
            max="5"
            value={newRating}
            onChange={(e) => setNewRating(e.target.value)}
            style={{ marginLeft: "10px", width: "50px" }}
          />
        </label>
        <button type="submit" style={{ marginLeft: "10px" }}>
          Submit Rating
        </button>
      </form>

      <div style={{ marginTop: "20px" }}>
        <Link to="/list">Back to My Albums</Link>
      </div>
    </div>
  );
}

export default DetailPage;
