import React, { useEffect, useState } from "react";
import { useParams, Link } from "react-router-dom";

function DetailPage() {
  const { id } = useParams();
  const [album, setAlbum] = useState(null);
  const [loading, setLoading] = useState(true);

  const [tracks, setTracks] = useState([]);
  const [tracksLoading, setTracksLoading] = useState(false);

  const [genres, setGenres] = useState([]);
  const [genresLoading, setGenresLoading] = useState(false);

  const [newRating, setNewRating] = useState("");

  const token = process.env.REACT_APP_SPOTIFY_TOKEN;

  useEffect(() => {
    async function fetchAlbum() {
      try {
        const response = await fetch(
          `https://6728860f270bd0b97555efb5.mockapi.io/albums/${id}`
        );
        if (!response.ok) {
          console.error(
            "Failed to load album",
            response.status,
            response.statusText
          );
          setLoading(false);
          return;
        }
        const data = await response.json();
        setAlbum(data);
      } catch (error) {
        console.error("Error fetching album:", error);
      } finally {
        setLoading(false);
      }
    }
    fetchAlbum();
  }, [id]);

  useEffect(() => {
    async function fetchTracks() {
      if (!album || !album.spotifyId || !token) return;

      setTracksLoading(true);
      try {
        const response = await fetch(
          `https://api.spotify.com/v1/albums/${album.spotifyId}/tracks`,
          {
            headers: { Authorization: `Bearer ${token}` },
          }
        );

        if (!response.ok) {
          console.error(
            "Failed to load tracks",
            response.status,
            response.statusText
          );
          setTracksLoading(false);
          return;
        }

        const data = await response.json();
        setTracks(data.items || []);
      } catch (error) {
        console.error("Error loading tracks:", error);
      } finally {
        setTracksLoading(false);
      }
    }

    if (album && token) {
      fetchTracks();
    }
  }, [album, token]);

  // Fetch artist genres after we have the album (and therefore the artist ID)
  useEffect(() => {
    async function fetchArtistGenres() {
      if (!album || !album.artists || album.artists.length === 0 || !token)
        return;

      setGenresLoading(true);
      const artistId = album.artists[0].id;
      try {
        const response = await fetch(
          `https://api.spotify.com/v1/artists/${artistId}`,
          {
            headers: { Authorization: `Bearer ${token}` },
          }
        );

        if (!response.ok) {
          console.error(
            "Failed to load artist genres",
            response.status,
            response.statusText
          );
          setGenresLoading(false);
          return;
        }

        const data = await response.json();
        setGenres(data.genres || []);
      } catch (error) {
        console.error("Error loading genres:", error);
      } finally {
        setGenresLoading(false);
      }
    }

    if (album && token) {
      fetchArtistGenres();
    }
  }, [album, token]);

  if (loading) {
    return <div>Loading album details...</div>;
  }

  if (!album) {
    return <div>Album not found.</div>;
  }

  const { name, album_type, artists, images, rating } = album;

  const artistNames =
    artists && artists.length > 0
      ? artists.map((artist) => artist.name).join(", ")
      : "Unknown Artist";

  const albumCover = images && images.length > 0 ? images[0].url : "";

  const handleRatingSubmit = async (e) => {
    e.preventDefault();
    const ratingValue = Number(newRating);

    if (!ratingValue || ratingValue < 1 || ratingValue > 5) {
      alert("Please enter a valid rating between 1 and 5");
      return;
    }

    try {
      const response = await fetch(
        `https://6728860f270bd0b97555efb5.mockapi.io/albums/${id}`,
        {
          method: "PATCH",
          headers: {
            "Content-Type": "application/json",
          },
          body: JSON.stringify({ rating: ratingValue }),
        }
      );

      if (!response.ok) {
        const errorText = await response.text();
        console.error("Failed to update rating:", errorText);
        alert("Failed to update rating.");
        return;
      }

      const updatedAlbum = await response.json();
      setAlbum(updatedAlbum);
      setNewRating("");
      alert("Rating updated successfully!");
    } catch (err) {
      console.error("Error updating rating:", err);
      alert("An error occurred while updating the rating.");
    }
  };

  return (
    <div style={{ padding: "20px" }}>
      <h1>{name}</h1>
      {albumCover && (
        <div style={{ marginBottom: "20px" }}>
          <img
            src={albumCover}
            alt={name}
            style={{ width: "300px", height: "auto", borderRadius: "8px" }}
          />
        </div>
      )}
      <p>
        <strong>Artist:</strong> {artistNames}
      </p>
      <p>
        <strong>Album Type:</strong> {album_type}
      </p>

      <h2>Genres</h2>
      {genresLoading && <p>Loading genres...</p>}
      {!genresLoading && genres.length === 0 && <p>No genres found.</p>}
      {!genresLoading && genres.length > 0 && (
        <ul>
          {genres.map((genre) => (
            <li key={genre}>{genre}</li>
          ))}
        </ul>
      )}

      <h2>Tracks</h2>
      {tracksLoading && <p>Loading tracks...</p>}
      {!tracksLoading && tracks.length === 0 && <p>No tracks found.</p>}
      {!tracksLoading && tracks.length > 0 && (
        <ol>
          {tracks.map((track) => (
            <li key={track.id}>{track.name}</li>
          ))}
        </ol>
      )}

      <h2>Rating</h2>
      {rating ? (
        <p>Current Rating: {rating} out of 5</p>
      ) : (
        <p>No rating yet. Be the first to rate this album.</p>
      )}

      <form onSubmit={handleRatingSubmit} style={{ marginTop: "10px" }}>
        <label>
          Set Rating (1-5):
          <input
            type="number"
            min="1"
            max="5"
            value={newRating}
            onChange={(e) => setNewRating(e.target.value)}
            style={{ marginLeft: "10px", width: "50px" }}
          />
        </label>
        <button type="submit" style={{ marginLeft: "10px" }}>
          Submit Rating
        </button>
      </form>

      <div style={{ marginTop: "20px" }}>
        <Link to="/list">Back to My Albums</Link>
      </div>
    </div>
  );
}

export default DetailPage;
