import React, { useState, useEffect } from "react";

const ItemForm = ({ addItem, editItem, updateItem }) => {
  const [formData, setFormData] = useState({
    name: "",
    attribute1: "",
    attribute2: "",
    attribute3: "",
  });

  useEffect(() => {
    if (editItem) {
      setFormData(editItem);
    }
  }, [editItem]);

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (editItem) {
      updateItem(formData);
    } else {
      addItem(formData);
    }
    setFormData({ name: "", attribute1: "", attribute2: "", attribute3: "" });
  };

  return (
    <div>
      <h2>{editItem ? "Edit Item" : "Add New Item"}</h2>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          name="name"
          placeholder="Name"
          value={formData.name}
          onChange={handleChange}
        />
        <input
          type="text"
          name="attribute1"
          placeholder="Attribute 1"
          value={formData.attribute1}
          onChange={handleChange}
        />
        <input
          type="text"
          name="attribute2"
          placeholder="Attribute 2"
          value={formData.attribute2}
          onChange={handleChange}
        />
        <input
          type="text"
          name="attribute3"
          placeholder="Attribute 3"
          value={formData.attribute3}
          onChange={handleChange}
        />
        <button type="submit">{editItem ? "Update Item" : "Add Item"}</button>
      </form>
    </div>
  );
};

export default ItemForm;
