import React, { useState, useEffect } from "react";
import axios from "axios";
import { Link, Outlet } from "react-router-dom";

const ShowList = () => {
  const [items, setItems] = useState([]);

  useEffect(() => {
    fetchItems();
  }, []);

  const fetchItems = async () => {
    try {
      const response = await axios.get(
        "https://6728860f270bd0b97555efb5.mockapi.io/courses"
      );
      setItems(response.data);
    } catch (error) {
      console.error("Error fetching items:", error);
    }
  };

  const deleteItem = async (id) => {
    try {
      await axios.delete(
        `https://6728860f270bd0b97555efb5.mockapi.io/courses/${id}`
      );
      setItems(items.filter((item) => item.id !== id));
    } catch (error) {
      console.error("Error deleting item:", error);
    }
  };

  return (
    <div className="container my-5">
      <h1 className="text-center mb-4">Course Management</h1>
      <Link to="/add" className="btn btn-primary mb-3">
        Add New Course
      </Link>

      <ul className="list-group">
        {items.map((item) => (
          <li
            key={item.id}
            className="list-group-item d-flex justify-content-between align-items-center"
          >
            <div>
              <strong>{item.cname}</strong> - {item.prof} - {item.credit}{" "}
              Credits - Code: {item.code}
            </div>
            <div>
              <Link
                to={`/edit/${item.id}`}
                className="btn btn-sm btn-primary mr-2"
              >
                Edit
              </Link>
              <button
                className="btn btn-sm btn-danger"
                onClick={() => deleteItem(item.id)}
              >
                Delete
              </button>
            </div>
          </li>
        ))}
      </ul>

      {/* Outlet for Add and Edit Routes */}
      <Outlet />
    </div>
  );
};

export default ShowList;
