import React, { useState, useEffect } from "react";
import AlbumItem from "../components/AlbumItem";

function SearchPage() {
  const [query, setQuery] = useState("");
  const [albums, setAlbums] = useState([]);
  const [token, setToken] = useState(process.env.REACT_APP_SPOTIFY_TOKEN);

  useEffect(() => {
    async function fetchToken() {
      const tk = await getSpotifyToken();
      setToken(tk);
    }
    fetchToken();
  }, []);

  const handleSearch = async (e) => {
    e.preventDefault();
    if (!query || !token) return;

    const response = await fetch(
      `https://api.spotify.com/v1/search?q=${encodeURIComponent(
        query
      )}&type=album`,
      {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      }
    );
    const data = await response.json();
    setAlbums(data.albums ? data.albums.items : []);
  };

  const handleAddAlbum = async (album) => {
    // First, fetch all existing albums from Mockapi.io
    const existingAlbumsResponse = await fetch(
      "https://6728860f270bd0b97555efb5.mockapi.io/albums"
    );
    const existingAlbums = await existingAlbumsResponse.json();

    // Check if the current album's id is already in existingAlbums
    const isDuplicate = existingAlbums.some(
      (storedAlbum) => storedAlbum.id === album.id
    );

    if (isDuplicate) {
      alert("This album has already been added.");
      return;
    }

    // If not duplicate, proceed to POST
    const response = await fetch(
      "https://6728860f270bd0b97555efb5.mockapi.io/albums",
      {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(album),
      }
    );

    if (response.ok) {
      alert("Album added successfully!");
    } else {
      alert("Failed to add album.");
    }
  };

  return (
    <div>
      <h1>Search Albums</h1>
      <form onSubmit={handleSearch}>
        <input
          value={query}
          onChange={(e) => setQuery(e.target.value)}
          placeholder="Search by album name"
        />
        <button type="submit">Search</button>
      </form>

      {albums.length > 0 && (
        <div>
          <h2>Results</h2>
          <ul>
            {albums.map((album) => (
              <li key={album.id}>
                <AlbumItem album={album} onAdd={() => handleAddAlbum(album)} />
              </li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
}

async function getSpotifyToken() {
  return process.env.REACT_APP_SPOTIFY_TOKEN;
}

export default SearchPage;
