import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";

function ListPage() {
  const [albums, setAlbums] = useState([]);
  const [searchQuery, setSearchQuery] = useState("");
  const [minRating, setMinRating] = useState("All"); // "All" means no rating filter

  useEffect(() => {
    async function fetchAlbums() {
      const response = await fetch(
        "https://6728860f270bd0b97555efb5.mockapi.io/albums"
      );
      const data = await response.json();
      setAlbums(data);
    }
    fetchAlbums();
  }, []);

  const gridContainerStyle = {
    display: "grid",
    gridTemplateColumns: "repeat(auto-fill, minmax(150px, 1fr))",
    gap: "16px",
    padding: "20px",
  };

  const albumCoverStyle = {
    width: "100%",
    height: "auto",
    objectFit: "cover",
    borderRadius: "8px",
    cursor: "pointer",
  };

  // Convert minRating to a number if not "All"
  const minRatingValue = minRating === "All" ? 0 : Number(minRating);

  const filteredAlbums = albums.filter((album) => {
    const albumName = album.name || "";

    const artistNames =
      album.artists && album.artists.length > 0
        ? album.artists.map((artist) => artist.name).join(" ")
        : "";

    const genreNames =
      album.genres && album.genres.length > 0 ? album.genres.join(" ") : "";

    const trackNames =
      album.tracks && album.tracks.length > 0
        ? album.tracks.map((track) => track.name).join(" ")
        : "";

    const searchableContent = [albumName, artistNames, genreNames, trackNames]
      .join(" ")
      .toLowerCase();

    const matchesSearch = searchableContent.includes(searchQuery.toLowerCase());

    // Apply rating filter only if minRating is not "All"
    const albumRating = album.rating || 0;
    const matchesRating = albumRating >= minRatingValue;

    return matchesSearch && matchesRating;
  });

  return (
    <div>
      <h1>My Albums</h1>
      <div style={{ margin: "20px" }}>
        <input
          type="text"
          placeholder="Search Albums"
          value={searchQuery}
          onChange={(e) => setSearchQuery(e.target.value)}
          style={{ padding: "8px", width: "300px", marginRight: "10px" }}
        />

        <select
          value={minRating}
          onChange={(e) => setMinRating(e.target.value)}
          style={{ padding: "8px" }}
        >
          <option value="All">All Ratings</option>
          <option value="1">1 and up</option>
          <option value="2">2 and up</option>
          <option value="3">3 and up</option>
          <option value="4">4 and up</option>
          <option value="5">5 only</option>
        </select>
      </div>
      {filteredAlbums.length === 0 ? (
        <p>No albums match.</p>
      ) : (
        <div style={gridContainerStyle}>
          {filteredAlbums.map((album) => {
            const albumCover =
              album.images && album.images[0] ? album.images[0].url : null;
            if (!albumCover) return null;

            return (
              <Link to={`/albums/${album.id}`} key={album.id}>
                <img
                  src={albumCover}
                  alt={album.name}
                  style={albumCoverStyle}
                />
              </Link>
            );
          })}
        </div>
      )}
    </div>
  );
}

export default ListPage;
