import React, { useState } from "react";
import { Link } from "react-router-dom";
import DetailPage from "./DetailPage";

function ListPage() {
  const [selectedAlbum, setSelectedAlbum] = useState(null);

  const albums = [
    // Example album data
    { id: 1, name: "Album 1" },
    { id: 2, name: "Album 2" },
    { id: 3, name: "Album 3" },
  ];

  const openModal = (albumId) => {
    setSelectedAlbum(albumId);
  };

  const closeModal = () => {
    setSelectedAlbum(null);
  };

  return (
    <div>
      <h1>Album List</h1>
      <ul>
        {albums.map((album) => (
          <li key={album.id}>
            <button onClick={() => openModal(album.id)}>{album.name}</button>
          </li>
        ))}
      </ul>
      {selectedAlbum && (
        <div
          style={{
            position: "fixed",
            top: 0,
            left: 0,
            width: "100%",
            height: "100%",
            backgroundColor: "rgba(0, 0, 0, 0.7)",
            display: "flex",
            justifyContent: "center",
            alignItems: "center",
            zIndex: 1000,
          }}
        >
          <div
            style={{
              backgroundColor: "#fff",
              width: "80%",
              height: "80%",
              overflow: "auto",
              position: "relative",
              borderRadius: "8px",
            }}
          >
            <button
              style={{
                position: "absolute",
                top: "10px",
                right: "10px",
                background: "red",
                color: "#fff",
                border: "none",
                borderRadius: "50%",
                width: "30px",
                height: "30px",
                cursor: "pointer",
                fontSize: "16px",
              }}
              onClick={closeModal}
            >
              Ã—
            </button>
            <DetailPage id={selectedAlbum} closeModal={closeModal} />
          </div>
        </div>
      )}
    </div>
  );
}

export default ListPage;
